try {
  if (typeof window !== 'undefined') {
    window.SPECIES_DATABASE = window.SPECIES_DATABASE || SPECIES_DATABASE;
  }
} catch (e) {
  // noop - best-effort exposure
}

// Helper functions
window.getSpeciesByCategory = function(category) {
  return Object.entries(SPECIES_DATABASE)
    .filter(([key, species]) => species.category === category)
    .reduce((obj, [key, species]) => {
      obj[key] = species;
      return obj;
    }, {});
};

window.getSpeciesForBadge = function(badgeType) {
  return Object.entries(SPECIES_DATABASE)
    .filter(([key, species]) => species.badgeCategories.includes(badgeType))
    .map(([key, species]) => key);
};